import React, { useState, useContext } from "react";
import PokemonContext from '../../Utils/PokemonContext'
import { Link } from "react-router-dom";
import './PokemonCard.css'

export default function PokemonCard({ id, name }) {
  const {addPokemonToCaughtList} = useContext(PokemonContext);

  return (
    <article className='PokemonCard'>
      <Link to={{ pathname: `/${id}` }}>
        <p>id: {id}</p>
        <p>name: {name ? name : "unknown"}</p>
      </Link>
      <button onClick={() => addPokemonToCaughtList}>Catch</button>
    </article>

  )
}
