import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from 'react-router-dom';
import LoadingSpinner from "../LoadingSpinner";
import './PokemonDetails.css'

export default function PokemonDetails({ storedPokemon }) {
    const { id } = useParams();

    const [pokemon, setPokemon] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios
            .get(`https://pokeapi.co/api/v2/pokemon/${id}`)
            // Extract the DATA from the received response
            .then((res) => {
                setPokemon(res.data);
                setLoading(false);
            });
    }, []);
    return (
        <article>
            {!loading ? pokemon && (
                <>
                    <p>id: {pokemon.id}</p>
                    <p>name: {pokemon.name}</p>
                    <img src={pokemon.sprites.other['official-artwork'].front_default} alt="pokemon image"></img>
                    <p>abilities: {pokemon.abilities.map((a, i) => a.ability.name + (i !== pokemon.abilities.length - 1 ? ', ' : ''))}</p>
                    <p>types: {pokemon.types.map((t, i) => t.type.name + (i !== pokemon.types.length - 1 ? ', ' : ''))}</p>
                    <p>{ /*storedPokemon(id) ? `Caught on ${getCaughtDate(detailsId)}` :*/ "Not caught yet" }</p>
                </>
            ) : (
                <LoadingSpinner />
            )}
        </article>
    )
}
