import { useState } from "react";
import { Link } from "react-router-dom";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import './PokemonCard.css';

export default function PokemonCard({ id, pokemon, storedPokemon, addPokemonToCaughtList, removePokemonFromCaughtList }) {
  const [iscaught, setIscaught] = useState(true);

  const handleFavoriteClick = () => {
    setIscaught(!iscaught)
    return !iscaught;
  };

  return (
    <article className='PokemonCard'>
      <Card sx={{ width: 200 }}>
        <Link to={{ pathname: `/${id}` }}>
          <CardContent>
            <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
              id: {id}
            </Typography>
            <Typography variant="h6" color="black">
            name: {pokemon.name ? pokemon.name : "unknown"}
            </Typography>

          </CardContent>
        </Link>
        <Button
          onClick={() => handleFavoriteClick ? removePokemonFromCaughtList(pokemon.id) : addPokemonToCaughtList(pokemon)}
          sx={{ marginBottom: 3, marginLeft: 2 }}
          size="small"
          variant={ !handleFavoriteClick ? "contained" : "outlined" }
        >
          {handleFavoriteClick ? "Release" : "Catch"}
        </Button>
      </Card>
    </article>

  )
}
